mapper.update:
UPDATE DAILY_EXPENSE SET 

		BUS_ID = #{busId}


		,FROM_DATE = #{fromDate}


		,TO_DATE = #{toDate}

		,PATH = #{path}
		,ON_PAPER_INCOME_LEAVE = #{onPaperIncomeLeave}
		,ON_PAPER_INCOME_RETURN = #{onPaperIncomeReturn}
		,IN_HAND_CASH = #{inHandCash}
		,EXTRA_INCOME = #{extraIncome}

		,REST_DAY = #{restDay}

	,updated_id = #{updatedId}
	,updated_datetime = NOW()
WHERE ID =#{id}

mapper.select:
SELECT b.license_plate AS bus, SUM(de.on_paper_income_leave + de.on_paper_income_return) AS totalIncome, SUM(ei.amount) AS totalExpense, SUM(de.extra_income) AS extraIncome, COUNT(DISTINCT(de.id)) AS totalRecords, SUM(de.rest_day) AS restDay FROM daily_expense de LEFT JOIN expense_item ei ON ei.daily_expense_id = de.id LEFT JOIN bus b ON de.bus_id = b.id
MONTH(de.from_date) = #{month} AND YEAR(de.from_date) = #{year}
GROUP BY b.license_plate
