mapper.update:
UPDATE DAILY_EXPENSE SET 

		BUS_ID = #{busId}


		,FROM_DATE = #{fromDate}


		,TO_DATE = #{toDate}

		,PATH = #{path}
		,ON_PAPER_INCOME_LEAVE = #{onPaperIncomeLeave}
		,ON_PAPER_INCOME_RETURN = #{onPaperIncomeReturn}
		,IN_HAND_CASH = #{inHandCash}
		,EXTRA_INCOME = #{extraIncome}

		,REST_DAY = #{restDay}

	,updated_id = #{updatedId}
	,updated_datetime = NOW()
WHERE ID =#{id}

mapper.select:
SELECT de.id, CONCAT(DATE_FORMAT(de.from_date,'%d/%m/%Y'), ' ~ ',DATE_FORMAT(de.to_date,'%d/%m/%Y')) AS dateRange, de.path, de.on_paper_income_leave AS onPaperIncomeLeave, de.on_paper_income_return AS onPaperIncomeReturn, de.in_hand_cash AS inHandCash, de.extra_income AS extraIncome, (SELECT SUM(item.amount) FROM expense_item item WHERE item.daily_expense_id = de.id) AS expenseTotal, de.rest_day AS restDay FROM daily_expense de
BUS_ID = #{busId}
AND de.from_date BETWEEN STR_TO_DATE(#{startDate}, '%d/%m/%Y') AND STR_TO_DATE(#{endDate}, '%d/%m/%Y')
AND (SELECT MAX(is_order) FROM destination WHERE FIND_IN_SET (id, de.path)) = #{isOrder}

SELECT b.license_plate AS bus, SUM(de.on_paper_income_leave + de.on_paper_income_return) AS totalIncome, (SELECT SUM(ei.amount) FROM expense_item ei WHERE ei.daily_expense_id) AS totalExpense, SUM(de.extra_income) AS extraIncome, SUM(CASE WHEN de.rest_day = 1 THEN 0 WHEN de.rest_day = 0 THEN 1 ELSE 0 END) AS roundTrips, SUM(CASE WHEN de.rest_day = 1 THEN ABS(DATEDIFF(de.to_date, de.from_date) + 1) WHEN de.rest_day = 0 THEN 0 ELSE 0 END) AS restDays FROM daily_expense de LEFT JOIN bus b ON de.bus_id = b.id
AND MONTH(de.from_date) = #{month} AND YEAR(de.from_date) = #{year}
AND b.id = #{busId}
AND (SELECT MAX(is_order) FROM destination WHERE FIND_IN_SET (id, de.path)) = #{isOrder}
GROUP BY b.license_plate
