mapper.update:
UPDATE PATH SET 

		PATH = #{path}

	,updated_id = #{updatedId}
	,updated_datetime = NOW()
WHERE ID =#{id}

mapper.select:
SELECT pe.expense_id AS expenseId, e.name, pe.amount
FROM path p LEFT JOIN path_expense pe ON pe.path_id = p.id LEFT JOIN path_bus pb ON pb.path_id = p.id LEFT JOIN expense_type e ON e.id = pe.expense_id
pb.bus_id = #{busId}
AND p.path = #{path}
