mapper.update:
UPDATE USER SET 

		USERNAME = #{username}


		,PASSWORD = #{password}


		,NAME = #{name}


		,ROLE = #{role}


		,STATUS = #{status}

WHERE ID =#{id}

UPDATE USER SET 
	last_logged_in = NOW()
WHERE ID =#{id}

mapper.select:
SELECT * FROM user 


			AND USERNAME = #{username}


			AND ID != #{notId}

		AND STATUS = 1
